import matplotlib.pyplot as plt

fig, ax = plt.subplots(1,4, figsize=(20,5))

fig.suptitle("Density Histograms of Pools Win Percentages by Fencer Weapon Classification");
bins = 15
fencer_stats['win_percentage'].hist(bins=bins, density=True, ax=ax[0]);
ax[0].set_title("All Fencers");
ax[0].set_xlabel("Win Percentage");
ax[0].set_ylabel("Density");
fencer_stats[fencer_stats['foil only']==True]['win_percentage'].hist(bins=bins, density=True, ax=ax[1]);
ax[1].set_title("Foil Only");
ax[1].set_xlabel("Win Percentage");
fencer_stats[fencer_stats['foil and']==True]['win_percentage'].hist(bins=bins, density=True, ax=ax[2]);
ax[2].set_title("Foil and");
ax[2].set_xlabel("Win Percentage");
fencer_stats[fencer_stats['no foil']==True]['win_percentage'].hist(bins=bins, density=True, ax=ax[3]);
ax[3].set_title("No Foil");
ax[3].set_xlabel("Win Percentage");

for cat in ["foil only","foil and","no foil"]:
    print(cat.title())
    print(fencer_stats[fencer_stats[cat]==True]['win_percentage'].describe())

import seaborn as sns

fencer_stats['weapon_category'] = fencer_stats[['foil only','foil and','no foil']].idxmax(axis='columns')
fencer_stats
ax = sns.violinplot(data=fencer_stats[['weapon_category','win_percentage']],
                    x="weapon_category", y="win_percentage", inner="quartile");
ax.set_xlabel("Weapon Category");
ax.set_ylabel("Win Percentage");
ax.set_title("Violin Plots of Win Percentages by Weapon Classification");

